mapper.select:
select good_feature_id,good_attr_id,good_feature_content from
good_feature inner join sku_feature on good_feature_id=feature_id
where skuid=#{sku_id}

select feature_id from
sku_feature inner join sku on sku_id=skuid where sku_num>0 and
of_goodid=#{good_id}

select * from sale_type
sale_type_id=#{typeid}
sale_type_id=(select sale_parent_typeid from sale_type where sale_type_id=#{typeid})

SELECT index0.sku_id FROM
(SELECT sku_id FROM sku INNER JOIN sku_feature ON sku_id=skuid WHERE feature_id=#{id} and of_goodid=#{goodid}) AS index${index}
ON index${index-1}.sku_id=index${index}.sku_id

select.foreach:
SELECT DISTINCT(feature_id) FROM sku_feature INNER JOIN good_feature ON good_feature_id=feature_id INNER JOIN sku ON sku_id=skuid WHERE of_goodid=#{goodid} AND good_attr_id NOT IN (SELECT good_attr_id FROM good_feature WHERE good_feature_id IN
#{id}
) AND feature_id NOT IN (SELECT feature_id FROM sku_feature INNER JOIN good_feature ON good_feature_id=feature_id INNER JOIN sku ON sku_id=skuid WHERE of_goodid=#{goodid} AND good_attr_id NOT IN (SELECT good_attr_id FROM good_feature WHERE good_feature_id IN #{id} ) AND skuid in(SELECT sku_id FROM (
SELECT sku_id,sku_num,feature_id FROM sku INNER JOIN sku_feature ON sku_id=skuid WHERE feature_id=#{id}
)sk GROUP BY sku_id HAVING COUNT(sku_id)>=${index+1})AND sku_num>0)
